export const Icon = ({
  name = '',
  size = 24,
  fill = 'none',
  stroke = 'black',
  strokeWidth = 2
}) => {
  return (
    <svg width={size} height={size} viewBox="0 0 24 24" fill={fill} xmlns="http://www.w3.org/2000/svg">
      { name === "home" &&
        <IconHome stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "star" &&
        <IconStar stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "verified" &&
        <IconVerified stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "close" &&
        <IconClose stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "delete" &&
        <IconDelete stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "search" &&
        <IconSearch stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "pin" &&
        <IconPin stroke={stroke} strokeWidth={strokeWidth}/>
      }
      { name === "notification" &&
        <IconNotification stroke={stroke} strokeWidth={strokeWidth}/>
      }
    </svg>
  )
}

